mapper.select:
select article_no, user_id, subject, content, hit, register_time, comment_cnt, good
from hotplace
where article_no = #{articleNo}

mapper.update:
update hotplace
set hit = hit + 1
where article_no = #{articleNo}

update hotplace
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}

mapper.select:
select save_folder, original_file, save_file
from file_info
where article_no = #{articleNo}
